    Yoga.nodeStyleSetWidth(root_child3, 30);
    Yoga.nodeStyleSetHeight(root_child3, 30);
    Yoga.nodeInsertChild(root, root_child3, 3);
    Yoga.nodeCalculateLayout(root, Yoga.Undefined, Yoga.Undefined, Yoga.DirectionLTR);

